import { useEffect } from 'react';

function EscapeListenerComponent() {

  useEffect(() => {
    // Функция, которая будет выполняться при нажатии клавиши "Esc"
    const handleEscape = (event: KeyboardEvent) => {
      if (event.key === 'Escape') {
        console.log('Нажата клавиша "Esc"');
      }
    };

    // Подписываемся на событие нажатия клавиши
    window.addEventListener('keydown', handleEscape);

    // Возвращаем функцию, которая выполнится при размонтировании компонента
    return () => {
      console.log('Компонент будет удалён, убираем подписку на клавишу "Esc"');
      window.removeEventListener('keydown', handleEscape); // Отменяем подписку
    };
  }, []); // Пустой массив зависимостей, эффект выполнится один раз при монтировании

  return (
    <div>
      <p>Нажми "Esc" и смотри консоль</p>
    </div>
  );
}

export default EscapeListenerComponent;
